import * as React from 'react';

import { RedlineExample } from './RedlineExample';
import { SpacingScale } from './SpacingScale';

export const PageSpacingAndLayout: React.FunctionComponent = () => {
    const { components } = useComponentsContext();
    const layoutComponentsToList = React.useMemo(() => {
        return components.filter(({ name }) => {
            if (
                name === 'Flex' ||
                name === 'FlexItem' ||
                name === 'Grid' ||
                name === 'GridItem' ||
                name === 'Spacing'
            ) {
                return true;
            }
            return false;
        });
    }, [components]);

    return (
        <EditorialPage pageSection="Foundations" pageName="Spacing & Layout">
            <Title as="h2" className={EDITORIAL_COPY_ELEMENT} id="spacing">
                Spacing
            </Title>
            <p>
                DRUIDS uses an <InlineCode>8px</InlineCode> scale for spacing
                between elements.
            </p>
            <p>
                Exceptions are at the extremes. The scale starts with{' '}
                <InlineCode>2px</InlineCode> and <InlineCode>4px</InlineCode> to
                allow for finer-grained margins when necessary, and it jumps{' '}
                <InlineCode>16px</InlineCode> between the two largest steps.
            </p>
            <Spacing marginY="xxl">
                <SpacingScale />
            </Spacing>
            <Paragraph size="sm" variant="secondary">
                Real-world example:
            </Paragraph>
            <Spacing as="div" marginTop="xs">
                <RedlineExample />
            </Spacing>
            <Title
                as="h3"
                className={EDITORIAL_COPY_ELEMENT}
                id="basic-spacing-components"
            >
                Basic spacing components
            </Title>
            <p>
                DRUIDS includes numerous components that make it easy to lay out
                elements on pages in a consistent, expected way.
            </p>
            <Spacing as="div" marginY="xxl">
                <Grid gap="xl" minWidth={348}>
                    {layoutComponentsToList.map((component) => (
                        <ComponentTile
                            illustration={component.illustration}
                            statusLevel={component.metadata.statusLevel}
                            link={`/components/${component.moduleName}/${component.name}`}
                            name={component.name}
                            description={component.metadata.description}
                            key={component.name}
                            showDescription
                        />
                    ))}
                </Grid>
            </Spacing>
            <Title
                as="h3"
                className={EDITORIAL_COPY_ELEMENT}
                id="how-to-import-spacing-values"
            >
                How to import spacing values
            </Title>
            <p>
                If you canâ€™t use the basic spacing components, the values are
                available to use directly.
            </p>
            <Grid minWidth={350}>
                <div>
                    <Paragraph size="sm" variant="secondary">
                        In Less:
                    </Paragraph>
                    <Code language="css" marginTop="xs">
                        {`@import (reference) '@druids/ui/ui-constants.less';

.myComponent {
    right: @spacing-sm;
}`}
                    </Code>
                </div>
                <div>
                    <Paragraph size="sm" variant="secondary">
                        In TypeScript:
                    </Paragraph>
                    <Code language="typescript" marginTop="xs">
                        {`import { SPACING_VALUES } from '@druids/ui/ui-constants';

// Donâ€™t set inline styles except as
// an absolute last resort
<div
    style={{
        right: SPACING_VALUES.md
    }}
/>`}
                    </Code>
                </div>
            </Grid>
            <hr />
            {IS_DRUIDS_PRIVATE && (
                <>
                    <Title
                        as="h2"
                        className={EDITORIAL_COPY_ELEMENT}
                        id="line-height"
                    >
                        Line height
                    </Title>
                    <EditorialAttentionMessage>
                        <p>Content coming soon!</p>
                    </EditorialAttentionMessage>
                    <hr />
                    <Title
                        as="h2"
                        className={EDITORIAL_COPY_ELEMENT}
                        id="page-layout"
                    >
                        Page layout
                    </Title>
                    <EditorialAttentionMessage>
                        <p>Content to come!</p>
                    </EditorialAttentionMessage>
                    <hr />
                </>
            )}
        </EditorialPage>
    );
};
