export const PageTypography: React.FunctionComponent = () => {
    const isDesktop = useDesktop();
    const { components } = useComponentsContext();
    const basicTypographyComponentsToList = React.useMemo(() => {
        return components.filter(({ name }) =>
            ['Text', 'Title', 'Code', 'Paragraph', 'List'].includes(name),
        );
    }, [components]);

    return (
        <EditorialPage pageSection="Foundations" pageName="Typography">
            <EditorialCopy
                size="lg"
                initialHeaderSize="xl"
                maxWidthType="text-only"
            >
                <Title
                    as="h2"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="about-side-panels"
                >
                    Typography in DRUIDS
                </Title>
                <p>
                    DRUIDS offers two types of typographic styles â€”{' '}
                    <Link
                        hasSmoothAnchorLink
                        url="#basic-typography"
                        title="Basic Typography Styles | DRUIDS"
                        isInline
                    >
                        Basic Basic Typography
                    </Link>{' '}
                    style, which includes most of the components inside the
                    Datadog app and{' '}
                    <Link
                        hasSmoothAnchorLink
                        url="#editorial-typography"
                        title="Editorial Typography Styles | DRUIDS"
                        isInline
                    >
                        Editorial Typography
                    </Link>{' '}
                    style, which is intended for specific parts of the app with
                    more extensive and elaborate typographic needs.
                </p>
                <p>
                    Both typography sets use the open-source typeface{' '}
                    <Link
                        url="https://fonts.google.com/specimen/Noto+Sans"
                        isExternal
                        hasExternalIcon
                        isInline
                    >
                        Noto Sans
                    </Link>{' '}
                    as a main typeface, and{' '}
                    <Link
                        url="https://fonts.google.com/specimen/Roboto+Mono"
                        isExternal
                        hasExternalIcon
                        isInline
                    >
                        Roboto Mono
                    </Link>{' '}
                    to render codes, and in all logs tables.
                </p>
            </EditorialCopy>
            <Flex
                justify="flex-start"
                alignItems="flex-start"
                gap="lg"
                direction={isDesktop ? 'row' : 'column'}
            >
                <div>
                    <EditorialCopy
                        size="lg"
                        initialHeaderSize="xl"
                        maxWidth="400px"
                    >
                        {isDesktop ? (
                            <>
                                <BasicTypographyImg />
                                <Title
                                    as="h3"
                                    className={EDITORIAL_COPY_ELEMENT}
                                    id="basic-typography-overview"
                                >
                                    Style 1: Basic Typography Components
                                </Title>
                            </>
                        ) : (
                            <>
                                <Title
                                    as="h3"
                                    className={EDITORIAL_COPY_ELEMENT}
                                    id="basic-typography-overview"
                                >
                                    Style 1: Basic Typography Components
                                </Title>
                                <BasicTypographyImg />
                            </>
                        )}
                        <p>
                            Basic Typography Components refers to and consists
                            of the majority of individual typography components,
                            such as{' '}
                            <Link url="/components/typography/Text" isInline>
                                Text
                            </Link>
                            ,{' '}
                            <Link url="/components/typography/Title" isInline>
                                Title
                            </Link>{' '}
                            and{' '}
                            <Link url="/components/typography/Code" isInline>
                                Code
                            </Link>{' '}
                            components, and often is used in more independent
                            and discrete occasions when compared to its
                            counterpart, Editorial Typography.
                        </p>
                        <p>
                            Basic Typographic Components are used readily
                            throughout the app when the typographic element is
                            supposed to be used on itâ€™s own, usually shorter in
                            length, and meant to be consumed as a discrete
                            readable unit or is in a smaller chunk of
                            text/information.
                        </p>
                        <p>
                            Another characteristic of Basic Typography
                            Components is the fact that there is no built in
                            spacing in between these individual elements, as
                            opposed to how Editorial Typography is set up.
                        </p>
                    </EditorialCopy>
                </div>
                <div>
                    <EditorialCopy
                        size="lg"
                        initialHeaderSize="xl"
                        maxWidth="400px"
                    >
                        {isDesktop ? (
                            <>
                                <EditorialTypographyImg />
                                <Title
                                    as="h3"
                                    className={EDITORIAL_COPY_ELEMENT}
                                    id="editorial-typography-overview"
                                >
                                    Style 2: Editorial Typography
                                </Title>
                            </>
                        ) : (
                            <>
                                <Title
                                    as="h3"
                                    className={EDITORIAL_COPY_ELEMENT}
                                    id="editorial-typography-overview"
                                >
                                    Style 2: Editorial Typography
                                </Title>
                                <EditorialTypographyImg />
                            </>
                        )}
                        <p>
                            Editorial Typography is designed to be used when the
                            content it holds is longer in length, generally more
                            lines of text, and it allows for a more dramatic,
                            graphic use of type in editorial context, in terms
                            of scale, spacing and layout.
                        </p>
                        <p>
                            It is accessible by a wrapper component,{' '}
                            <Link
                                url="/components/typography/EditorialCopy"
                                isInline
                            >
                                EditorialCopy
                            </Link>
                            , which wraps naked HTML typography elements such as{' '}
                            <InlineCode>h1</InlineCode>â€“
                            <InlineCode>h6</InlineCode> header tags, and many
                            others.
                        </p>
                    </EditorialCopy>
                </div>
            </Flex>
            <EditorialCopy
                size="lg"
                initialHeaderSize="xl"
                marginTop="xl"
                maxWidthType="text-only"
            >
                <HorizontalSeparator />
                <Title
                    as="h2"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="basic-typography"
                >
                    Basic Typography
                </Title>
                <p>
                    Basic Typography Components include all of the typography
                    components, except for the{' '}
                    <Link url="/components/typography/EditorialCopy" isInline>
                        EditorialCopy
                    </Link>{' '}
                    is designed to be used for more extensive blocks of text.
                </p>
                <BasicTypographyExampleImg />
                <p>
                    Basic Typography Components are widely used inside other
                    components as a supporting element such as in{' '}
                    <Link
                        url="/components/dialogs/KeyboardShortcutList"
                        isInline
                    >
                        KeyboardShortcutList
                    </Link>
                    ,{' '}
                    <Link url="/components/nav/PaginationSummary" isInline>
                        PaginationSummary
                    </Link>
                    ,{' '}
                    <Link url="/components/time/TimeRangeIndicator" isInline>
                        TimeRangeIndicator
                    </Link>{' '}
                    and many other components with short blocks of typographic
                    elements.
                </p>
                <Title as="h3" className={EDITORIAL_COPY_ELEMENT}>
                    DRUIDS Typography Font-Size / Line-Height Scale
                </Title>
                <ScaleExample />
                <Title
                    as="h3"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="examples-of-basic-typography-components"
                >
                    Examples of Layout / Page using Basic Typography Components
                </Title>
                <BasicTypographyExample1Img />
                <BasicTypographyExample2Img />

                <Title
                    as="h3"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="key-basic-typography-components"
                >
                    Key Basic Typography Components
                </Title>
                <Spacing as="div" marginY={isDesktop ? 'xxl' : 'sm'}>
                    <Grid gap="xl" minWidth={348}>
                        {basicTypographyComponentsToList.map((component) => (
                            <ComponentTile
                                illustration={component.illustration}
                                statusLevel={component.metadata.statusLevel}
                                link={`/components/${component.moduleName}/${component.name}`}
                                name={component.name}
                                description={component.metadata.description}
                                key={component.name}
                                showDescription
                            />
                        ))}
                    </Grid>
                </Spacing>
                <HorizontalSeparator />
                <Title
                    as="h2"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="editorial-typography"
                >
                    Editorial Typography
                </Title>
                <p>
                    Editorial Typography set is meant to be used in places where
                    thereâ€™s more than just a few lines of copy. The typography
                    set contains optimized pre-set configurations of typographic
                    elements such as the relationship between font sizes, line
                    heights, vertical spacing, letter spacing, and word spacing
                    so the userâ€™s focus is not distracted from the content.
                </p>
                <p>
                    When HTML elements are wrapped inside the{' '}
                    <Link url="/components/typography/EditorialCopy" isInline>
                        EditorialCopy
                    </Link>{' '}
                    component, preset values of spacings in between containing
                    elements and font-size hierarchy in between different
                    headlines and body text is automatically applied, which
                    helps designers and developers to use the component
                    conveniently without having to redesign the size / spacing
                    in a custom way.
                </p>
                <Title as="h3" className={EDITORIAL_COPY_ELEMENT}>
                    Editorial Typography Header Scale
                </Title>
                <HeaderExample />
                <Title as="h3" className={EDITORIAL_COPY_ELEMENT}>
                    Editorial Typography Paragraph Scale
                </Title>
                <ParagraphExample />
                <Title
                    as="h3"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="examples-of-layouts-that-uses-editorial-copy"
                >
                    Examples of Layouts that are using Editorial Typography
                </Title>
                <EditorialTypographyExample1Img />
                <EditorialTypographyExample3Img />
                <EditorialTypographyExample2Img />
                <HorizontalSeparator />
                <Title
                    as="h2"
                    className={EDITORIAL_COPY_ELEMENT}
                    id="typefaces"
                >
                    Typefaces
                </Title>
            </EditorialCopy>
            <Flex
                justify="flex-start"
                alignItems="flex-start"
                gap="lg"
                direction={isDesktop ? 'row' : 'column'}
            >
                <div>
                    <EditorialCopy
                        size="lg"
                        initialHeaderSize="xl"
                        maxWidth="400px"
                    >
                        <Title
                            as="h3"
                            className={EDITORIAL_COPY_ELEMENT}
                            id="noto-sans"
                        >
                            Noto Sans
                        </Title>
                        <TypefacesNotoSans />
                        <p>
                            The primary DRUIDS typeface is a sans-serif called{' '}
                            <Link
                                url="https://fonts.google.com/specimen/Noto+Sans"
                                isExternal
                                hasExternalIcon
                                isInline
                            >
                                Noto Sans
                            </Link>
                            . It was commissioned by Google to bedesigned with
                            the goal of achieving visual harmony and supporting
                            a wide range of characters and glyphs to remove all
                            substitute characters (which is called â€œtofuâ€,
                            therefore â€œNo more Tofuâ€). It is one of the most
                            widely used typefaces on the web because of its
                            flexibility.
                        </p>
                    </EditorialCopy>
                </div>
                <div>
                    <EditorialCopy
                        size="lg"
                        initialHeaderSize="xl"
                        maxWidth="400px"
                    >
                        <Title
                            as="h3"
                            className={EDITORIAL_COPY_ELEMENT}
                            id="roboto-mono"
                        >
                            Roboto Mono: Code
                        </Title>
                        <TypefacesRobotoMono />
                        <p>
                            To display code snippets and logs, DRUIDS uses a
                            monospace font called{' '}
                            <Link
                                url="https://fonts.google.com/specimen/Roboto+Mono"
                                isExternal
                                hasExternalIcon
                                isInline
                            >
                                Roboto Mono
                            </Link>
                            . Roboto Mono was originally developed by Google for
                            its mobile operating system, Android, in 2011 and
                            was designed with the goal of creating a
                            approachable and emotional typeface.
                        </p>
                    </EditorialCopy>
                </div>
            </Flex>
        </EditorialPage>
    );
};
